INSERT INTO `wp_posts` VALUES ('1720', '1', '2016-11-07 15:31:32', '2016-11-07 08:31:32', 'This content is before the <a title=\"The More Tag\" href=\"http://en.support.wordpress.com/splitting-content/more-tag/\" target=\"_blank\">more tag</a>.\n\nRight after this sentence should be a \"continue reading\" button of some sort.\n\n<!--more-->\n\nAnd this content is after the more tag.', 'Template: More Tag', '', 'inherit', 'closed', 'closed', '', '996-revision-v1', '', '', '2016-11-07 15:31:32', '2016-11-07 08:31:32', '', '996', 'http://trangchu.dev/khong-phan-loai/996-revision-v1/', '0', 'revision', '', '0');
