INSERT INTO `wp_posts` VALUES ('155', '1', '2007-09-04 10:47:47', '2007-09-04 17:47:47', 'Repository-hosted Themes are required to support display of comments on static Pages as well as on single blog Posts. This static Page has comments, and these comments should be displayed.\nIf the Theme includes a custom option to prevent static Pages from displaying comments, such option must be disabled (i.e. so that static Pages display comments) by default.\nAlso, verify that this Page does not display taxonomy information (e.g. categories or tags) ortime-stampinformation (Page publish date/time).', 'Page with comments', '', 'trash', 'open', 'closed', '', 'page-with-comments__trashed', '', '', '2016-11-07 15:53:10', '2016-11-07 08:53:10', '', '53', 'http://wpthemetestdata.wordpress.com/page-with-comments/', '3', 'page', '', '3');
