INSERT INTO `wp_posts` VALUES ('1762', '1', '2016-11-07 15:53:10', '2016-11-07 08:53:10', 'Repository-hosted Themes are required to support display of comments on static Pages as well as on single blog Posts. This static Page has comments, and these comments should be displayed.\nIf the Theme includes a custom option to prevent static Pages from displaying comments, such option must be disabled (i.e. so that static Pages display comments) by default.\nAlso, verify that this Page does not display taxonomy information (e.g. categories or tags) ortime-stampinformation (Page publish date/time).', 'Page with comments', '', 'inherit', 'closed', 'closed', '', '155-revision-v1', '', '', '2016-11-07 15:53:10', '2016-11-07 08:53:10', '', '155', 'http://trangchu.dev/khong-phan-loai/155-revision-v1/', '0', 'revision', '', '0');
